// Cart.js
import React from "react";
import "./Cart.css"; // Стилизация корзины

const Cart = ({ cart, removeFromCart, clearCart }) => {
  // Вычисление общей стоимости товаров в корзине
  const totalPrice = cart.reduce((total, item) => total + parseFloat(item.price.replace(" грн.", "").replace(",", ".")), 0);

  return (
    <div className="cart-container">
      <h2>Корзина</h2>
      {cart.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        <div>
          <ul>
            {cart.map((item, index) => (
              <li key={index} className="cart-item">
                <img src={item.image} alt={item.name} className="cart-item-image" />
                <div className="cart-item-info">
                  <span>{item.name}</span>
                  <span>{item.price}</span>
                  <button onClick={() => removeFromCart(index)}>Удалить</button>
                </div>
              </li>
            ))}
          </ul>
          <div className="cart-total">
            <p>Общая стоимость: {totalPrice.toFixed(2)} грн.</p>
            <button className="clear-cart" onClick={clearCart}>Очистить корзину</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
