import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

const parsePrice = (priceStr) => {
  if (!priceStr) return 0;
  const cleanStr = priceStr.replace(/\s|грн\.|₴/gi, '').replace(/,/g, '');
  return parseFloat(cleanStr) || 0;
};

const Checkout = ({ cart }) => {
  const navigate = useNavigate();

  const [form, setForm] = useState({
    name: '',
    city: '',
    postOffice: '',
    phone: '',
    email: '',
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const subtotal = cart.reduce(
    (sum, item) => sum + parsePrice(item.price) * item.quantity,
    0
  );

  const handleSubmit = (e) => {
    e.preventDefault();
    // Здесь можешь добавить валидацию и отправку данных
    alert('Заказ подтверждён! Спасибо за покупку.');
    // Можно очистить корзину или перенаправить куда-то
    navigate('/'); // к примеру, на главную
  };

  return (
    <div className="checkout-container" style={{ maxWidth: 600, margin: '0 auto', padding: 20 }}>
      <h1>Оформление заказа</h1>
      <form onSubmit={handleSubmit} className="checkout-form">
        <label>
          Имя *<br />
          <input type="text" name="name" value={form.name} onChange={handleChange} required />
        </label>
        <label>
          Город *<br />
          <input type="text" name="city" value={form.city} onChange={handleChange} required />
        </label>
        <label>
          Отделение Новой почты *<br />
          <input type="text" name="postOffice" value={form.postOffice} onChange={handleChange} required />
        </label>
        <label>
          Телефон *<br />
          <input type="tel" name="phone" value={form.phone} onChange={handleChange} required />
        </label>
        <label>
          Email *<br />
          <input type="email" name="email" value={form.email} onChange={handleChange} required />
        </label>

        <h2>Ваш заказ</h2>
        <table className="cart-table" style={{ width: '100%', marginBottom: 20 }}>
          <thead>
            <tr>
              <th>Товар</th>
              <th className="text-center">Количество</th>
              <th className="text-center">Цена</th>
              <th className="text-center">Подытог</th>
            </tr>
          </thead>
          <tbody>
            {cart.map((item, index) => (
              <tr key={index}>
                <td>{item.name}</td>
                <td className="text-center">{item.quantity}</td>
                <td className="text-center">{parsePrice(item.price).toFixed(2)} ₴</td>
                <td className="text-center">{(parsePrice(item.price) * item.quantity).toFixed(2)} ₴</td>
              </tr>
            ))}
          </tbody>
        </table>

        <p style={{ fontWeight: 'bold', fontSize: '1.2em' }}>
          Итого: {subtotal.toFixed(2)} ₴
        </p>

        <div style={{ marginTop: 20, marginBottom: 20 }}>
          <h3>Прямой банковский перевод</h3>
          <p>
            Свяжитесь с консультантом для уточнения деталей оплаты и доставки.
            Спасибо, что выбрали наш сервис.
          </p>
        </div>

        <button type="submit" className="confirm-btn" style={{ padding: '10px 20px', fontSize: 16 }}>
          Подтвердить заказ
        </button>
      </form>
    </div>
  );
};

export default Checkout;
