import React, { useState } from 'react';
import products from './products';
import './Showcase.css';

const Showcase = ({ addToCart }) => {
  const [addedItems, setAddedItems] = useState({});
  const [sortOrder, setSortOrder] = useState(null); // null = без сортировки

  const handleAddToCart = (product) => {
    addToCart(product);
    setAddedItems((prev) => ({ ...prev, [product.name]: true }));
    setTimeout(() => {
      setAddedItems((prev) => ({ ...prev, [product.name]: false }));
    }, 1000);
  };

  const getDisplayedProducts = () => {
    if (!sortOrder) return products; // без сортировки
    const sorted = [...products].sort((a, b) => {
      const priceA = parseFloat(a.price.replace(/[^\d.]/g, ''));
      const priceB = parseFloat(b.price.replace(/[^\d.]/g, ''));
      return sortOrder === 'asc' ? priceA - priceB : priceB - priceA;
    });
    return sorted;
  };

  const displayedProducts = getDisplayedProducts();

  return (
    <div className="showcase">
      <h1 className="text-xl font-bold mb-4">Товары</h1>

      <div className="sort-buttons mb-4 space-x-2">
        <button onClick={() => setSortOrder(null)}>По умолчанию</button>
        <button onClick={() => setSortOrder('asc')}>По возрастанию ↑</button>
        <button onClick={() => setSortOrder('desc')}>По убыванию ↓</button>
      </div>

      <div className="products">
        {displayedProducts.map((product) => (
          <div key={product.name} className="product">
            <img src={product.image} alt={product.name} />
            <div className="product-info">
              <h3>{product.name}</h3>
              <p className="product-price">{product.price} ₴</p>
              <button
                onClick={() => handleAddToCart(product)}
                className={`add-button ${addedItems[product.name] ? 'added' : ''}`}
              >
                {addedItems[product.name] ? '✔ Добавлено!' : 'Добавить в корзину'}
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Showcase;
