import React, { useState } from "react";
import "./Showcase.css"; // Создайте файл CSS для стилизации
import { useNavigate } from 'react-router-dom';
import products from "./products";

const Showcase = () => {
  const [cart, setCart] = useState([]);
  const [isOrderFormVisible, setIsOrderFormVisible] = useState(false);

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.name === product.name);
      if (existingProduct) {
        return prevCart.map((item) =>
          item.name === product.name ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  const removeFromCart = (productToRemove) => {
    setCart((prevCart) => {
      const updatedCart = prevCart.map((item) =>
        item.name === productToRemove.name && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      );
      return updatedCart.filter((item) => item.quantity > 0);
    });
  };

  const deleteFromCart = (productToRemove) => {
    setCart((prevCart) => {
      return prevCart.filter((item) => item.name !== productToRemove.name);
    });
  };

  const getTotalPrice = () => {
    return cart.reduce((total, product) => {
      const price = parseFloat(product.price.replace(" грн.", "").replace(",", ""));
      return total + price * product.quantity;
    }, 0).toFixed(2);
  };

  const calculateDeliveryCost = (location) => {
    return 85; // Новая почта
  };

  const handleOrderButtonClick = () => {
    setIsOrderFormVisible(true); // Показать форму
  };

  const handleFormSubmit = (event) => {
    event.preventDefault();
    console.log('Форма заказа отправлена!');
    setIsOrderFormVisible(false); // Скрыть форму после отправки
  };

  return (
    <div className="showcase">
      <h1>Товары</h1>
      <div className="products">
        {products.map((product, index) => (
          <div key={index} className="product">
            <img src={product.image} alt={product.name} />
            <div className="product-info">
              <h3>{product.name}</h3>
              <p>{product.price}</p>
              <button onClick={() => addToCart(product)}>Добавить в корзину</button>
            </div>
          </div>
        ))}
      </div>

      <div className="cart">
        <h2>Корзина</h2>
        {cart.length === 0 ? (
          <p>Ваша корзина пуста.</p>
        ) : (
          <div>
            <table className="cart-table">
              <thead>
                <tr>
                  <th>Удалить товар</th>
                  <th>Миниатюра</th>
                  <th>Товар</th>
                  <th>Цена</th>
                  <th>Количество</th>
                  <th>Подытог</th>
                </tr>
              </thead>
              <tbody>
                {cart.map((item, index) => (
                  <tr key={index}>
                    <td>
                      <button onClick={() => deleteFromCart(item)}>×</button>
                    </td>
                    <td>
                      <img src={item.image} alt={item.name} width="50" />
                    </td>
                    <td>{item.name}</td>
                    <td>{item.price}</td>
                    <td>
                      <div className="cart-buttons">
                        <button onClick={() => addToCart(item)}>+</button>
                        <span>{item.quantity}</span>
                        <button onClick={() => removeFromCart(item)}>-</button>
                      </div>
                    </td>
                    <td>{(parseFloat(item.price.replace(" грн.", "").replace(",", "")) * item.quantity).toFixed(2)} грн.</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="total">
              <h3>Сумма заказов: {getTotalPrice()} грн.</h3>
              <div>
                <h4>Доставка:</h4>
                <p>Новая почта: {calculateDeliveryCost(location)} грн.</p>
              </div>
              <div className="total-price">
                <h3>Итого: {(parseFloat(getTotalPrice()) + calculateDeliveryCost(location)).toFixed(2)} грн.</h3>
              </div>
            </div>
            <button onClick={handleOrderButtonClick}>Оформить заказ</button>
          </div>
        )}
      </div>

      {/* Форма для оформления заказа */}
      {isOrderFormVisible && (
        <div className="order-form">
          <h3>Оформление заказа</h3>
          <form onSubmit={handleFormSubmit}>
            <div className="form-group">
              <label>Имя</label>
              <input type="text" required />
            </div>
            <div className="form-group">
              <label>Город</label>
              <input type="text" required />
            </div>
            <div className="form-group">
              <label>Отделение Новой почты</label>
              <input type="text" required />
            </div>
            <div className="form-group">
              <label>Телефон</label>
              <input type="tel" required />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input type="email" required />
            </div>
            <div className="form-group">
              <label>Телеграмм</label>
              <input type="text" />
            </div>
            <div className="form-actions">
              <button type="submit">Подтвердить заказ</button>
              <button type="button" onClick={() => setIsOrderFormVisible(false)}>Отмена</button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default Showcase;
