import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './Cart.css';

const parsePrice = (priceStr) => {
  if (!priceStr) return 0;
  // Убираем пробелы, "грн.", "₴" и запятые (тысячные разделители)
  const cleanStr = priceStr
    .replace(/\s|грн\.|₴/gi, '') // убираем пробелы, "грн." и "₴"
    .replace(/,/g, '');          // убираем запятые
  return parseFloat(cleanStr) || 0;
};

const Cart = ({ cart, addToCart, removeFromCart, clearCart }) => {
  const [deliveryMethod, setDeliveryMethod] = useState('nova-poshta');
  const deliveryPrices = {
    'nova-poshta': 100,
    ems: 100,
    courier: 120,
  };

  const subtotal = cart.reduce(
    (sum, item) => sum + parsePrice(item.price) * item.quantity,
    0
  );
  const deliveryCost = deliveryPrices[deliveryMethod] || 0;
  const total = subtotal + deliveryCost;

  return (
    <div className="cart-container">
      <h1 className="cart-title">Корзина</h1>

      {cart.length === 0 ? (
        <p className="cart-empty">Корзина пуста</p>
      ) : (
        <>
          <table className="cart-table">
            <thead>
              <tr>
                <th>Товар</th>
                <th className="text-center">Цена</th>
                <th className="text-center">Количество</th>
                <th className="text-center">Подытог</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item, index) => (
                <tr key={index}>
                  <td>{item.name}</td>
                  <td className="text-center">
                    {parsePrice(item.price).toFixed(2)} ₴
                  </td>
                  <td className="text-center">
                    <div className="quantity-controls">
                      <button
                        onClick={() => removeFromCart(item)}
                        className="quantity-btn"
                      >
                        −
                      </button>
                      <span>{item.quantity}</span>
                      <button
                        onClick={() => addToCart(item)}
                        className="quantity-btn"
                      >
                        +
                      </button>
                    </div>
                  </td>
                  <td className="text-center">
                    {(parsePrice(item.price) * item.quantity).toFixed(2)} ₴
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="delivery-section">
            <h2>Выберите способ доставки</h2>
            <label className="delivery-option">
              <input
                type="radio"
                name="delivery"
                value="nova-poshta"
                checked={deliveryMethod === 'nova-poshta'}
                onChange={() => setDeliveryMethod('nova-poshta')}
              />{' '}
              Новая Почта — 100 ₴
            </label>
            <label className="delivery-option">
              <input
                type="radio"
                name="delivery"
                value="ems"
                checked={deliveryMethod === 'ems'}
                onChange={() => setDeliveryMethod('ems')}
              />{' '}
              EMS — 100 ₴
            </label>
            <label className="delivery-option">
              <input
                type="radio"
                name="delivery"
                value="courier"
                checked={deliveryMethod === 'courier'}
                onChange={() => setDeliveryMethod('courier')}
              />{' '}
              Курьерская доставка — 120 ₴
            </label>
            <p className="delivery-info">
              Варианты доставки будут обновлены при оформлении заказа.
            </p>
          </div>

          <div className="totals">
            <p>Подытог: {subtotal.toFixed(2)} ₴</p>
            <p>Доставка: {deliveryCost.toFixed(2)} ₴</p>
            <p className="total-amount">Итого: {total.toFixed(2)} ₴</p>
          </div>

          <div className="buttons-row">
            <button onClick={clearCart} className="clear-btn">
              Очистить корзину
            </button>
            <Link to="/checkout" className="checkout-link">
              Оформить заказ
            </Link>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
