import React, { useState } from "react";
import "./Showcase.css"; // Создайте файл CSS для стилизации

const Cart = ({}) => {

  const addToCart = (product) => {
    setCart((prevCart) => {
      const existingProduct = prevCart.find((item) => item.name === product.name);
      if (existingProduct) {
        return prevCart.map((item) =>
          item.name === product.name ? { ...item, quantity: item.quantity + 1 } : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  const removeFromCart = (productToRemove) => {
    setCart((prevCart) => {
      const updatedCart = prevCart.map((item) =>
        item.name === productToRemove.name && item.quantity > 1
          ? { ...item, quantity: item.quantity - 1 }
          : item
      );
      return updatedCart.filter((item) => item.quantity > 0);
    });
  };

  const deleteFromCart = (productToRemove) => {
    setCart((prevCart) => {
      return prevCart.filter((item) => item.name !== productToRemove.name);
    });
  };

  const getTotalPrice = () => {
    return cart.reduce((total, product) => {
      const price = parseFloat(product.price.replace(" грн.", "").replace(",", ""));
      return total + price * product.quantity;
    }, 0).toFixed(2);
  };

  const calculateDeliveryCost = (location) => {
    return 85; // Новая почта
  };

  const handleOrderButtonClick = () => {
    setIsOrderFormVisible(true); // Показать форму
  };

  return (
    <div className="cart">
        <h2>Корзина</h2>
        {cart.length === 0 ? (
          <p>Ваша корзина пуста.</p>
        ) : (
          <div>
            <table className="cart-table">
              <thead>
                <tr>
                  <th>Удалить товар</th>
                  <th>Миниатюра</th>
                  <th>Товар</th>
                  <th>Цена</th>
                  <th>Количество</th>
                  <th>Подытог</th>
                </tr>
              </thead>
              <tbody>
                {cart.map((item, index) => (
                  <tr key={index}>
                    <td>
                      <button onClick={() => deleteFromCart(item)}>×</button>
                    </td>
                    <td>
                      <img src={item.image} alt={item.name} width="50" />
                    </td>
                    <td>{item.name}</td>
                    <td>{item.price}</td>
                    <td>
                      <div className="cart-buttons">
                        <button onClick={() => addToCart(item)}>+</button>
                        <span>{item.quantity}</span>
                        <button onClick={() => removeFromCart(item)}>-</button>
                      </div>
                    </td>
                    <td>{(parseFloat(item.price.replace(" грн.", "").replace(",", "")) * item.quantity).toFixed(2)} грн.</td>
                  </tr>
                ))}
              </tbody>
            </table>
            <div className="total">
              <h3>Сумма заказов: {getTotalPrice()} грн.</h3>
              <div>
                <h4>Доставка:</h4>
                <p>Новая почта: {calculateDeliveryCost(location)} грн.</p>
              </div>
              <div className="total-price">
                <h3>Итого: {(parseFloat(getTotalPrice()) + calculateDeliveryCost(location)).toFixed(2)} грн.</h3>
              </div>
            </div>
            <button onClick={handleOrderButtonClick}>Оформить заказ</button>
          </div>
        )}
      </div>
  );
};

export default Cart;
