import React from 'react';
import { Link } from 'react-router-dom';

const Cart = ({ cart, addToCart, removeFromCart, clearCart }) => {
  const total = cart.reduce((sum, item) => sum + item.price * item.quantity, 0);

  return (
    <div className="container">
      <h1 className="text-xl font-bold mb-4">Корзина</h1>

      {cart.length === 0 ? (
        <p>Корзина пуста</p>
      ) : (
        <>
          <ul className="space-y-2">
            {cart.map((item, index) => (
              <li key={index} className="border p-4 rounded flex justify-between items-center">
                <div>
                  <h2 className="font-semibold">{item.name}</h2>
                  <p>{item.price} ₴ × {item.quantity}</p>
                </div>
                <div className="flex items-center gap-2">
                  <button onClick={() => removeFromCart(item)} className="bg-red-500 text-white px-2 py-1 rounded">
                    −
                  </button>
                  <span>{item.quantity}</span>
                  <button onClick={() => addToCart(item)} className="bg-green-500 text-white px-2 py-1 rounded">
                    +
                  </button>
                </div>
              </li>
            ))}
          </ul>
          <div className="mt-4">
            <p className="font-bold text-lg">Итого: {total.toFixed(2)} ₴</p>
            <div className="flex gap-4 mt-2">
              <button onClick={clearCart} className="bg-gray-300 px-4 py-2 rounded">
                Очистить корзину
              </button>
              <Link
                to="/checkout"
                className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600"
              >
                Оформить заказ
              </Link>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
