import React, { useState } from 'react';
import './ChatWidget.css';
import widgetImage from '../assets/widget.png'; // переименуй загруженное изображение и положи сюда

const ChatWidget = () => {
  const [open, setOpen] = useState(false);

  return (
    <div className={`chat-widget ${open ? 'open' : ''}`}>
      <div className="chat-tab" onClick={() => setOpen(!open)}>
        Онлайн консультант
      </div>
      {open && (
        <div className="chat-box">
          <button className="close-btn" onClick={() => setOpen(false)}>×</button>
          <img src={widgetImage} alt="We are here" className="chat-image" />
        </div>
      )}
    </div>
  );
};

export default ChatWidget;
