import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const parsePrice = (priceStr) => {
  if (!priceStr) return 0;
  const cleanStr = priceStr.replace(/\s|грн\.|₴/g, '').replace(/,/g, '');
  return parseFloat(cleanStr) || 0;
};

const Cart = ({ cart, addToCart, removeFromCart, clearCart }) => {
  const [deliveryMethod, setDeliveryMethod] = useState('nova-poshta');
  const deliveryPrices = {
    'nova-poshta': 100,
    ems: 100,
    courier: 120,
  };

  const subtotal = cart.reduce(
    (sum, item) => sum + parsePrice(item.price) * item.quantity,
    0
  );
  const deliveryCost = deliveryPrices[deliveryMethod] || 0;
  const total = subtotal + deliveryCost;

  return (
    <div className="max-w-5xl mx-auto p-6 bg-white rounded-lg shadow-lg mt-10">
      <h1 className="text-3xl font-extrabold mb-8 text-gray-800 border-b pb-4">
        Ваша корзина
      </h1>

      {cart.length === 0 ? (
        <p className="text-center text-gray-500 text-lg py-20">
          Корзина пуста
        </p>
      ) : (
        <>
          <table className="w-full border-collapse table-fixed">
            <thead>
              <tr className="bg-gradient-to-r from-blue-500 to-blue-600 text-white uppercase text-sm tracking-wider">
                <th className="p-4 text-left">Товар</th>
                <th className="p-4 w-32 text-center">Цена</th>
                <th className="p-4 w-32 text-center">Количество</th>
                <th className="p-4 w-40 text-center">Подытог</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item, index) => (
                <tr
                  key={index}
                  className="border-b hover:bg-gray-50 transition-colors duration-200"
                >
                  <td className="p-4 text-gray-800 font-medium">{item.name}</td>
                  <td className="p-4 text-center text-gray-700">
                    {parsePrice(item.price).toFixed(2)} ₴
                  </td>
                  <td className="p-4 text-center">
                    <div className="inline-flex items-center border rounded-lg overflow-hidden select-none">
                      <button
                        onClick={() => removeFromCart(item)}
                        className="px-3 py-1 bg-red-600 hover:bg-red-700 text-white font-bold transition"
                        aria-label="Уменьшить количество"
                      >
                        −
                      </button>
                      <span className="px-4 py-1 text-gray-900 font-semibold min-w-[30px] text-center">
                        {item.quantity}
                      </span>
                      <button
                        onClick={() => addToCart(item)}
                        className="px-3 py-1 bg-green-600 hover:bg-green-700 text-white font-bold transition"
                        aria-label="Увеличить количество"
                      >
                        +
                      </button>
                    </div>
                  </td>
                  <td className="p-4 text-center font-semibold text-gray-900">
                    {(parsePrice(item.price) * item.quantity).toFixed(2)} ₴
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <section className="mt-10 bg-blue-50 p-6 rounded-lg shadow-inner">
            <h2 className="text-xl font-semibold mb-4 text-blue-800">
              Выберите способ доставки
            </h2>
            <div className="flex flex-col gap-3 max-w-sm">
              {Object.entries(deliveryPrices).map(([key, price]) => {
                const label =
                  key === 'nova-poshta'
                    ? 'Новая Почта'
                    : key === 'ems'
                    ? 'EMS'
                    : 'Курьерская доставка';
                return (
                  <label
                    key={key}
                    className="cursor-pointer flex items-center gap-3 p-3 rounded-md bg-white hover:bg-blue-100 transition-shadow shadow-sm"
                  >
                    <input
                      type="radio"
                      name="delivery"
                      value={key}
                      checked={deliveryMethod === key}
                      onChange={() => setDeliveryMethod(key)}
                      className="accent-blue-600 w-5 h-5"
                    />
                    <span className="text-blue-900 font-medium">
                      {label} — {price} ₴
                    </span>
                  </label>
                );
              })}
            </div>
            <p className="text-sm text-blue-700 mt-3 italic">
              Цены и варианты доставки уточняются при оформлении заказа.
            </p>
          </section>

          <div className="mt-10 flex flex-col md:flex-row md:justify-end md:items-center gap-6">
            <div className="text-right space-y-1">
              <p className="text-lg text-gray-700">
                Подытог: <span className="font-semibold">{subtotal.toFixed(2)} ₴</span>
              </p>
              <p className="text-lg text-gray-700">
                Доставка: <span className="font-semibold">{deliveryCost.toFixed(2)} ₴</span>
              </p>
              <p className="text-2xl font-extrabold text-gray-900">
                Итого: {total.toFixed(2)} ₴
              </p>
            </div>

            <div className="flex gap-4">
              <button
                onClick={clearCart}
                className="px-6 py-3 bg-gray-300 hover:bg-gray-400 rounded-lg font-semibold transition"
                aria-label="Очистить корзину"
              >
                Очистить корзину
              </button>

              <Link
                to="/checkout"
                className="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-semibold shadow-lg transition"
                aria-label="Перейти к оформлению заказа"
              >
                Оформить заказ
              </Link>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
