import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const parsePrice = (priceStr) => {
  if (!priceStr) return 0;
  const cleanStr = priceStr.replace(/\s|грн\.|₴/g, '').replace(/,/g, '');
  return parseFloat(cleanStr) || 0;
};

const Cart = ({ cart, addToCart, removeFromCart, clearCart }) => {
  const [deliveryMethod, setDeliveryMethod] = useState('nova-poshta');
  const deliveryPrices = {
    'nova-poshta': 100,
    ems: 100,
    courier: 120,
  };

  const subtotal = cart.reduce(
    (sum, item) => sum + parsePrice(item.price) * item.quantity,
    0
  );
  const deliveryCost = deliveryPrices[deliveryMethod] || 0;
  const total = subtotal + deliveryCost;

  return (
    <div className="max-w-4xl mx-auto p-4 bg-gray-50 rounded shadow">
      <h1 className="text-2xl font-bold mb-6">Корзина</h1>

      {cart.length === 0 ? (
        <p className="text-gray-600">Корзина пуста</p>
      ) : (
        <>
          <table className="w-full table-auto mb-4 bg-white border border-gray-300">
            <thead className="bg-gray-200">
              <tr>
                <th className="p-2 text-left">Товар</th>
                <th className="p-2">Цена</th>
                <th className="p-2">Количество</th>
                <th className="p-2">Подытог</th>
              </tr>
            </thead>
            <tbody>
              {cart.map((item, index) => (
                <tr key={index} className="border-t">
                  <td className="p-2">{item.name}</td>
                  <td className="p-2 text-center">
                    {parsePrice(item.price).toFixed(2)} ₴
                  </td>
                  <td className="p-2 text-center">
                    <div className="flex items-center justify-center gap-2">
                      <button
                        onClick={() => removeFromCart(item)}
                        className="bg-red-500 text-white px-2 py-1 rounded"
                      >
                        −
                      </button>
                      <span>{item.quantity}</span>
                      <button
                        onClick={() => addToCart(item)}
                        className="bg-green-500 text-white px-2 py-1 rounded"
                      >
                        +
                      </button>
                    </div>
                  </td>
                  <td className="p-2 text-center">
                    {(parsePrice(item.price) * item.quantity).toFixed(2)} ₴
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="bg-white p-4 border border-gray-300 rounded mb-4">
            <h2 className="text-lg font-semibold mb-2">Выберите способ доставки</h2>
            <div className="space-y-2">
              <label className="block">
                <input
                  type="radio"
                  name="delivery"
                  value="nova-poshta"
                  checked={deliveryMethod === 'nova-poshta'}
                  onChange={() => setDeliveryMethod('nova-poshta')}
                />
                {' '}
                Новая Почта — 100 ₴
              </label>
              <label className="block">
                <input
                  type="radio"
                  name="delivery"
                  value="ems"
                  checked={deliveryMethod === 'ems'}
                  onChange={() => setDeliveryMethod('ems')}
                />
                {' '}
                EMS — 100 ₴
              </label>
              <label className="block">
                <input
                  type="radio"
                  name="delivery"
                  value="courier"
                  checked={deliveryMethod === 'courier'}
                  onChange={() => setDeliveryMethod('courier')}
                />
                {' '}
                Курьерская доставка — 120 ₴
              </label>
            </div>
            <p className="text-sm text-gray-600 mt-2">
              Варианты доставки будут обновлены при оформлении заказа.
            </p>
          </div>

          <div className="text-right">
            <p className="text-lg font-semibold">Подытог: {subtotal.toFixed(2)} ₴</p>
            <p className="text-lg font-semibold">Доставка: {deliveryCost.toFixed(2)} ₴</p>
            <p className="text-xl font-bold mb-4">Итого: {total.toFixed(2)} ₴</p>

            <div className="flex gap-4 justify-end">
              <button
                onClick={clearCart}
                className="bg-gray-300 px-4 py-2 rounded hover:bg-gray-400"
              >
                Очистить корзину
              </button>
              <Link
                to="/checkout"
                className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
              >
                Оформить заказ
              </Link>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default Cart;
