import React, { useState } from 'react';
import './Checkout.css'; // стили для формы

const Checkout = ({ cart }) => {
  const [formData, setFormData] = useState({
    name: '',
    city: '',
    novaPoshtaBranch: '',
    phone: '',
    email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Здесь можно добавить логику отправки данных или перехода дальше
    alert('Спасибо за ваш заказ!');
  };

  const total = cart.reduce(
    (sum, item) => sum + parseFloat(item.price.replace(/[^\d.]/g, '')) * item.quantity,
    0
  );

  return (
    <div className="checkout-container">
      <h1>Оформление заказа</h1>
      <form className="checkout-form" onSubmit={handleSubmit}>
        <label>
          Имя
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
            placeholder="Введите ваше имя"
          />
        </label>

        <label>
          Город
          <input
            type="text"
            name="city"
            value={formData.city}
            onChange={handleChange}
            required
            placeholder="Введите город"
          />
        </label>

        <label>
          Отделение Новой почты
          <input
            type="text"
            name="novaPoshtaBranch"
            value={formData.novaPoshtaBranch}
            onChange={handleChange}
            required
            placeholder="Введите отделение"
          />
        </label>

        <label>
          Телефон
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
            placeholder="+380 ХХХ ХХХ ХХ ХХ"
          />
        </label>

        <label>
          Email
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            placeholder="example@mail.com"
          />
        </label>

        <h2>Ваш заказ</h2>
        <ul className="order-list">
          {cart.map((item, idx) => (
            <li key={idx}>
              {item.name} — {item.quantity} × {parseFloat(item.price.replace(/[^\d.]/g, '')).toFixed(2)} ₴
            </li>
          ))}
        </ul>

        <p className="order-total">Итого: {total.toFixed(2)} ₴</p>

        <div className="payment-info">
          <h3>Прямой банковский перевод</h3>
          <p>
            Свяжитесь с консультантом для уточнения деталей оплаты и доставки.
            Спасибо, что выбрали наш сервис.
          </p>
        </div>

        <button type="submit" className="confirm-btn">
          Подтвердить заказ
        </button>
      </form>
    </div>
  );
};

export default Checkout;
